mapper.select:
select
  gb_id, gb_name, gb_description, gb_start, gb_end
from
  group_buying

    gb_status = 1

        and gb_start >= #{startTime}


        and gb_end <= #{endTime}


limit #{index}, #{limit}

select count(*) from group_buying
gb_status = 1
and gb_start >= #{startTime}
and gb_end <= #{endTime}
