mapper.select:
select product_id, product_name, product_intro, product_price, product_color, product_version, product_size, product_sales, product_max, product_url, product_time, sc_id from product
product_status = 1
and sc_id = #{scId}
and product_name like CONCAT(CONCAT('%',#{productName},'%'))
limit #{index}, #{limit}
