mapper.select:
select
  seckill_id, seckill_name, seckill_description, seckill_start, seckill_end
from
  seckill

    seckill_status = 1

        and seckill_start >= #{startTime}


        and seckill_end <= #{endTime}


limit #{index}, #{limit}

select count(*) from seckill
seckill_status = 1
and seckill_start >= #{startTime}
and seckill_end <= #{endTime}
